import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import './Inbox.css';

const Inbox = ({ user }) => {
    const [chats, setChats] = useState([]);
    const [selectedChat, setSelectedChat] = useState(null);
    const history = useHistory();

    useEffect(() => {
        axios.get(`http://localhost:8081/chats/${user.id}`)
            .then(res => {
                setChats(res.data);
            })
            .catch(err => {
                console.error("Error fetching chats:", err);
            });
    }, [user.id]);

    const handleChatClick = (chat) => {
        setSelectedChat(chat);
        history.push(`/inbox/${chat.id}`);
    };

    return (
        <div className="inbox-container">
            <div className="inbox-chats">
                {chats.map(chat => (
                    <div
                        key={chat.id}
                        className="inbox-chat"
                        onClick={() => handleChatClick(chat)}
                    >
                        <img src={`http://localhost:8081/${chat.profile_pic}`} alt={chat.name} className="inbox-chat-pic" />
                        <div>
                            <h3>{chat.name}</h3>
                            <p>{chat.lastMessage}</p>
                        </div>
                    </div>
                ))}
            </div>
            <div className="inbox-messages">
                {selectedChat && (
                    <Chat chatId={selectedChat.id} selectedChat={selectedChat} />
                )}
            </div>
        </div>
    );
};

export default Inbox;
