import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Inbox.css';

const Inbox = ({ userId }) => {
    const [chats, setChats] = useState([]);
    const [activeChat, setActiveChat] = useState(null);
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState('');

    useEffect(() => {
        const fetchChats = async () => {
            const response = await axios.get(`http://localhost:8081/inbox/${userId}`);
            setChats(response.data);
        };
        fetchChats();
    }, [userId]);

    const fetchMessages = async (chatId) => {
        const response = await axios.get(`http://localhost:8081/messages/${chatId}`);
        setMessages(response.data);
        setActiveChat(chatId);
    };

    const handleSendMessage = async () => {
        if (message.trim() === '') return;

        const newMessage = {
            chat_id: activeChat,
            user_email: userId,
            message: message,
        };

        await axios.post('http://localhost:8081/messages', newMessage);
        setMessages([...messages, newMessage]);
        setMessage('');
    };

    return (
        <div className="inbox-container">
            <div className="inbox-list">
                {chats.map((chat) => (
                    <div key={chat.id} className="chat-item" onClick={() => fetchMessages(chat.id)}>
                        <img src={`http://localhost:8081/${chat.profile_pic}`} alt="Profile" className="profile-pic" />
                        <div>
                            <div className="chat-user-name">{chat.name}</div>
                            <div className="chat-last-message">{chat.lastMessage}</div>
                        </div>
                    </div>
                ))}
            </div>
            <div className="chat-container">
                {activeChat && (
                    <>
                        <div className="chat-header">
                            <img src={`http://localhost:8081/${chats.find(chat => chat.id === activeChat).profile_pic}`} alt="Profile" className="profile-pic" />
                            <div className="chat-user-name">{chats.find(chat => chat.id === activeChat).name}</div>
                        </div>
                        <div className="messages-list">
                            {messages.map((msg, index) => (
                                <div key={index} className="message-item">
                                    {msg.postImage && (
                                        <div className="post-image-container">
                                            <img src={`http://localhost:8081/${msg.postImage}`} alt="Post" className="post-image" />
                                            <div className="post-title">{msg.postTitle}</div>
                                        </div>
                                    )}
                                    <div className="message-content">
                                        <span>{msg.message}</span>
                                        <span className="message-timestamp">{new Date(msg.timestamp).toLocaleString()}</span>
                                    </div>
                                </div>
                            ))}
                        </div>
                        <div className="chat-input-container">
                            <input
                                type="text"
                                value={message}
                                onChange={(e) => setMessage(e.target.value)}
                                placeholder="Type your message..."
                                className="chat-input"
                            />
                            <button onClick={handleSendMessage} className="send-button">Send</button>
                        </div>
                    </>
                )}
            </div>
        </div>
    );
};

export default Inbox;
