import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Home.css';

const Inbox = () => {
    const [inbox, setInbox] = useState([]);
    const user = JSON.parse(localStorage.getItem('user'));

    useEffect(() => {
        if (!user || !user.id) {
            console.error("User not found or user ID missing");
            return;
        }

        console.log(`Fetching inbox for user ID: ${user.id}`);
        axios.get(`http://localhost:8081/chats/${user.id}`)
            .then(res => {
                console.log("Fetched inbox data: ", res.data);
                setInbox(res.data);
            })
            .catch(err => {
                console.error("Error fetching inbox: ", err);
            });
    }, [user]);

    return (
        <div className="inbox-container">
            <h1>Inbox</h1>
            <div className="inbox-list">
                {inbox.length > 0 ? (
                    inbox.map(chat => (
                        <div className="inbox-item" key={chat.id}>
                            {chat.name}
                        </div>
                    ))
                ) : (
                    <p>No messages</p>
                )}
            </div>
        </div>
    );
};

export default Inbox;
