import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './Chat.css';

function Chat() {
    const { chatId } = useParams();
    const [messages, setMessages] = useState([]);
    const userId = JSON.parse(localStorage.getItem('user')).id;

    useEffect(() => {
        const fetchMessages = async () => {
            try {
                const response = await axios.get(`http://localhost:8081/messages/${chatId}`);
                setMessages(response.data);
            } catch (error) {
                console.error('Error fetching messages:', error);
            }
        };

        fetchMessages();
    }, [chatId]);

    const handleSendMessage = async (content) => {
        try {
            await axios.post(`http://localhost:8081/messages`, {
                content,
                buyer_id: userId,
                seller_id: chatId
            });
            fetchMessages();
        } catch (error) {
            console.error('Error sending message:', error);
        }
    };

    return (
        <div className="chat-container">
            <div className="messages">
                {messages.map((message, index) => (
                    <div key={index} className={`message ${message.buyer_id === userId ? 'sent' : 'received'}`}>
                        <p>{message.content}</p>
                    </div>
                ))}
            </div>
            <div className="input-container">
                <input type="text" placeholder="Type your message..." onKeyDown={(e) => {
                    if (e.key === 'Enter') {
                        handleSendMessage(e.target.value);
                        e.target.value = '';
                    }
                }} />
            </div>
        </div>
    );
}

export default Chat;
