import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser } from '@fortawesome/free-solid-svg-icons';
import './Profile.css';

function Profile() {
    const [user, setUser] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const userData = localStorage.getItem('user');
        if (userData) {
            setUser(JSON.parse(userData));
        } else {
            navigate('/'); // Redirect to login if user data is not found
        }
    }, [navigate]);

    const handleLogout = () => {
        localStorage.removeItem('user');
        navigate('/');
    };

    if (!user) {
        return <div>Loading...</div>;
    }

    return (
        <div className="profile-container mt-5">
            <h2><FontAwesomeIcon icon={faUser} /> Profile</h2>
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <button className="btn btn-secondary" onClick={() => navigate('/settings')}>
                Edit Profile
            </button>
            <button className="btn btn-primary mt-3">Publish Post</button>
            <button className="btn btn-danger mt-3" onClick={handleLogout}>Log Out</button>
        </div>
    );
}

export default Profile;
