import React from 'react';
import './Login.css';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';

function Login() {
    const validationSchema = Yup.object().shape({
        email: Yup.string().required('Email is required').email('Email is invalid'),
        password: Yup.string().required('Password is required'),
    });

    const { register, handleSubmit, formState: { errors } } = useForm({
        resolver: yupResolver(validationSchema)
    });

    function onSubmit(data) {
        console.log(data);
    }

    return (
        <div className="login-wrapper">
            <div className="login-container">
                <h1>Book-Swap</h1>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <div className="form-group">
                        <input name="email" type="text" {...register('email')} placeholder="Email" className={`form-control ${errors.email ? 'is-invalid' : ''}`} />
                        <div className="invalid-feedback">{errors.email?.message}</div>
                    </div>
                    <div className="form-group">
                        <input name="password" type="password" {...register('password')} placeholder="Password" className={`form-control ${errors.password ? 'is-invalid' : ''}`} />
                        <div className="invalid-feedback">{errors.password?.message}</div>
                    </div>
                    <button type="submit" className="btn btn-primary">Log in</button>
                </form>
            </div>
            <div className="bottom-box">
                <p>Don't have an account? <a href="/signup">Sign up</a></p>
            </div>
        </div>
    );
}

export default Login;
