import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './PostDetail.css';

function OwnPostDetail() {
    const { id } = useParams();
    const [post, setPost] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchPost = async () => {
            try {
                const response = await axios.get(`http://localhost:8081/post/${id}`);
                setPost(response.data);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching the post:', error);
                setLoading(false);
            }
        };

        fetchPost();
    }, [id]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (!post) {
        return <div>Error loading post details. Please try again later.</div>;
    }

    return (
        <div className="own-post-detail">
            <div className="post-header">
                <h1>{post.title}</h1>
                <p>by {post.author}</p>
            </div>
            <div className="post-content">
                <img src={`http://localhost:8081/${post.picture}`} alt={post.title} />
                <p className="post-description">{post.description}</p>
                <p className="post-price">Price: â‚¬{post.price}</p>
                <p className="post-user">Posted by: {post.user_name}</p>
            </div>
        </div>
    );
}

export default OwnPostDetail;
