import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Inbox.css';

function Inbox() {
    const [chats, setChats] = useState([]);
    const navigate = useNavigate();

    const user = JSON.parse(localStorage.getItem('user'));

    useEffect(() => {
        fetchChats();
    }, []);

    const fetchChats = async () => {
        try {
            const res = await axios.get(`http://localhost:8081/getChats/${user.id}`);
            const chatData = await Promise.all(
                res.data.map(async (chat) => {
                    const otherUserId = chat.buyer_id === user.id ? chat.seller_id : chat.buyer_id;
                    const otherUserRes = await axios.get(`http://localhost:8081/getUserById/${otherUserId}`);
                    return { ...chat, otherUser: otherUserRes.data };
                })
            );
            setChats(chatData);
        } catch (err) {
            console.error("Error fetching chats:", err);
        }
    };

    return (
        <div className="inbox-container">
            <h2>Inbox</h2>
            {chats.length === 0 ? (
                <p>No chats available</p>
            ) : (
                <ul className="chat-list">
                    {chats.map((chat) => (
                        <li key={chat.id} onClick={() => navigate(`/chat/${chat.id}`)}>
                            {chat.otherUser.name}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}

export default Inbox;
