import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Post.css';

function Post({ post }) {
  const [userName, setUserName] = useState('Unknown');

  useEffect(() => {
    axios.get(`http://localhost:8081/user/${post.user_email}`)
      .then(res => {
        setUserName(res.data.name);
      })
      .catch(err => {
        console.error(`Error fetching user data for ${post.user_email}:`, err);
      });
  }, [post.user_email]);

  return (
    <div className="post-card">
      <a href={`/post/${post.id}`}>
        <img src={`http://localhost:8081/${post.picture}`} alt={post.title} className="post-image" />
      </a>
      <div className="post-details">
        <a href={`/post/${post.id}`} className="post-title">{post.title}</a>
        <p className="post-author">Author: {post.author}</p>
        <p className="post-user">Posted by: {userName}</p>
        <p className="post-price">Price: â‚¬{parseFloat(post.price).toFixed(2)}</p>
        <p className="post-genre">Genre: {post.genre}</p>
      </div>
    </div>
  );
}

export default Post;
