import React, { useState } from 'react';
import axios from 'axios';
import Layout from './Layout';
import './CreatePost.css';

function CreatePost() {
    const [title, setTitle] = useState('');
    const [author, setAuthor] = useState('');
    const [description, setDescription] = useState('');
    const [price, setPrice] = useState('');
    const [genre, setGenre] = useState('');
    const [selectedImage, setSelectedImage] = useState(null);
    const [errorMessage, setErrorMessage] = useState('');
    const [showDiscardModal, setShowDiscardModal] = useState(false);

    const validateInputs = () => {
        const inputs = document.querySelectorAll('input, select');
        let isValid = true;
        inputs.forEach(input => {
            if (!input.checkValidity()) {
                isValid = false;
                input.classList.add('invalid');
                setTimeout(() => {
                    input.classList.remove('invalid');
                }, 500);
            }
        });
        return isValid;
    };

    const handleSharePost = () => {
        if (!validateInputs()) {
            setErrorMessage('Please fill out all fields correctly.');
            return;
        }

        const formData = new FormData();
        formData.append('title', title);
        formData.append('author', author);
        formData.append('description', description);
        formData.append('price', price);
        formData.append('genre', genre);
        formData.append('image', selectedImage);

        axios.post('http://localhost:8081/create-post', formData)
            .then(response => {
                console.log(response.data);
                // Handle success logic here
            })
            .catch(error => {
                console.error(error);
                // Handle error logic here
            });
    };

    const handleCancelPost = () => {
        setShowDiscardModal(true);
    };

    const handleDiscardPost = () => {
        setShowDiscardModal(false);
        // Handle discard logic here
    };

    return (
        <Layout>
            <div className="create-post-container">
                <h2>Publish a Post</h2>
                {errorMessage && <p className="error-message">{errorMessage}</p>}
                <div className="form-group">
                    <input
                        type="text"
                        placeholder="Title"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <input
                        type="text"
                        placeholder="Author"
                        value={author}
                        onChange={(e) => setAuthor(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <textarea
                        placeholder="Description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <input
                        type="number"
                        placeholder="Price"
                        value={price}
                        onChange={(e) => setPrice(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <select
                        value={genre}
                        onChange={(e) => setGenre(e.target.value)}
                        required
                    >
                        <option value="">Select Genre</option>
                        <option value="Fiction">Fiction</option>
                        <option value="Non-Fiction">Non-Fiction</option>
                        <option value="Science">Science</option>
                        <option value="History">History</option>
                        {/* Add more genres as needed */}
                    </select>
                </div>
                <div className="form-group">
                    <div
                        className="image-upload"
                        onDrop={(e) => {
                            e.preventDefault();
                            setSelectedImage(e.dataTransfer.files[0]);
                        }}
                        onDragOver={(e) => e.preventDefault()}
                    >
                        {selectedImage ? (
                            <div className="image-preview">
                                <img
                                    src={URL.createObjectURL(selectedImage)}
                                    alt="Selected"
                                    className="selected-image"
                                />
                                <button
                                    className="remove-image-button"
                                    onClick={() => setSelectedImage(null)}
                                >
                                    Remove
                                </button>
                            </div>
                        ) : (
                            <p>Add or Drag & Drop an image below</p>
                        )}
                    </div>
                </div>
                <button className="btn btn-primary mt-2" onClick={handleSharePost}>Share</button>
                <button className="btn btn-secondary mt-2" onClick={handleCancelPost}>Cancel</button>

                {showDiscardModal && (
                    <div className="modal">
                        <div className="modal-content">
                            <p>Discard post?</p>
                            <p>If you leave, your edits won't be saved.</p>
                            <button className="btn btn-danger mt-2" onClick={handleDiscardPost}>Discard</button>
                            <button className="btn btn-secondary mt-2" onClick={() => setShowDiscardModal(false)}>Cancel</button>
                        </div>
                    </div>
                )}
            </div>
        </Layout>
    );
}

export default CreatePost;
