import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Inbox.css';

const Inbox = () => {
    const [chats, setChats] = useState([]);
    const userId = localStorage.getItem('userId');
    const navigate = useNavigate();

    useEffect(() => {
        if (!userId) {
            console.log('User not found or user ID missing');
            return;
        }

        const fetchInbox = async () => {
            try {
                const response = await axios.get(`http://localhost:8081/chats/${userId}`);
                setChats(response.data);
            } catch (error) {
                console.error('Error fetching inbox:', error);
            }
        };

        fetchInbox();
    }, [userId]);

    const handleChatClick = (sellerId) => {
        navigate(`/chat/${sellerId}`);
    };

    return (
        <div className="inbox-container">
            <h1>Inbox</h1>
            {chats.map((chat) => (
                <div key={chat.seller_id} className="chat-item" onClick={() => handleChatClick(chat.seller_id)}>
                    {chat.seller_name}
                </div>
            ))}
        </div>
    );
};

export default Inbox;
