// src/Pages/CreatePost.js or src/Pages/CreatePost.tsx
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const CreatePost = () => {
    const [selectedImage, setSelectedImage] = useState(null);
    const [description, setDescription] = useState('');
    const navigate = useNavigate();

    const handleImageChange = (e) => {
        if (e.target.files && e.target.files[0]) {
            setSelectedImage(e.target.files[0]);
        }
    };

    const handleSharePost = () => {
        const formData = new FormData();
        formData.append('image', selectedImage);
        formData.append('description', description);
        formData.append('email', JSON.parse(localStorage.getItem('user')).email);

        axios.post('http://localhost:8081/create-post', formData)
            .then(res => {
                if (res.data.message === "Success") {
                    navigate('/profile');
                } else {
                    console.log("Failed to create post");
                }
            })
            .catch(err => {
                console.log("Error occurred while creating post", err);
            });
    };

    return (
        <div className="modal">
            <div className="modal-content">
                {!selectedImage ? (
                    <>
                        <h3>Upload an image</h3>
                        <input type="file" onChange={handleImageChange} />
                    </>
                ) : (
                    <>
                        <img src={URL.createObjectURL(selectedImage)} alt="Selected" className="image-preview" />
                        <textarea
                            placeholder="Write a caption..."
                            value={description}
                            onChange={(e) => setDescription(e.target.value)}
                            className="form-control mt-2"
                        ></textarea>
                        <button className="btn btn-primary mt-2" onClick={handleSharePost}>Share</button>
                    </>
                )}
                <button className="btn btn-secondary mt-2" onClick={() => navigate('/profile')}>Cancel</button>
            </div>
        </div>
    );
};

export default CreatePost;
