import React, { useState } from 'react';
import axios from 'axios';

function CreatePost() {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [price, setPrice] = useState('');
    const [picture, setPicture] = useState(null);
    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        setErrorMessage('');
        setSuccessMessage('');

        if (!title || !description || !price) {
            setErrorMessage('Title, description, and price are required');
            return;
        }

        const formData = new FormData();
        formData.append('title', title);
        formData.append('description', description);
        formData.append('price', price);
        formData.append('email', 'user@example.com'); // Replace with actual user email
        if (picture) {
            formData.append('picture', picture);
        }

        axios.post('http://localhost:8081/create-post', formData)
            .then(res => {
                if (res.data.message === "Post created successfully") {
                    setSuccessMessage('Post created successfully');
                    setTitle('');
                    setDescription('');
                    setPrice('');
                    setPicture(null);
                } else {
                    setErrorMessage('Error creating post');
                }
            })
            .catch(err => {
                setErrorMessage('Error creating post');
                console.error(err);
            });
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>Title</label>
                <input
                    type="text"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                />
            </div>
            <div>
                <label>Description</label>
                <input
                    type="text"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                />
            </div>
            <div>
                <label>Price</label>
                <input
                    type="number"
                    step="0.01"
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                />
            </div>
            <div>
                <label>Picture</label>
                <input
                    type="file"
                    onChange={(e) => setPicture(e.target.files[0])}
                />
            </div>
            {errorMessage && <p>{errorMessage}</p>}
            {successMessage && <p>{successMessage}</p>}
            <button type="submit">Create Post</button>
        </form>
    );
}

export default CreatePost;
