import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './Chat.css';

function Chat() {
    const { id } = useParams();
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState('');
    const user = JSON.parse(localStorage.getItem('user'));

    useEffect(() => {
        fetchChatData();
    }, []);

    const fetchChatData = async () => {
        try {
            const response = await axios.get(`http://localhost:8081/getMessages/${id}`);
            setMessages(response.data);
        } catch (error) {
            console.error('Error fetching chat data', error);
        }
    };

    const sendMessage = async () => {
        try {
            await axios.post('http://localhost:8081/sendMessage', {
                chat_id: id,
                user_email: user.email,
                message: message,
            });
            setMessage('');
            fetchChatData();
        } catch (error) {
            console.error('Failed to send message', error);
        }
    };

    return (
        <div className="chat-container">
            <div className="message-list">
                {messages.map((msg, index) => (
                    <div key={index} className={`message-item ${msg.user_email === user.email ? 'own-message' : ''}`}>
                        <span>{msg.user_email}</span>
                        <p>{msg.message}</p>
                    </div>
                ))}
            </div>
            <div className="message-input">
                <input
                    type="text"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    placeholder="Type your message..."
                />
                <button onClick={sendMessage}>Send</button>
            </div>
        </div>
    );
}

export default Chat;
