import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './CreatePost.css';

function CreatePost({ user, fetchPosts, setShowModal, editingPostId, setEditingPostId, initialTitle, initialDescription, initialPrice }) {
    const [title, setTitle] = useState(initialTitle || '');
    const [description, setDescription] = useState(initialDescription || '');
    const [price, setPrice] = useState(initialPrice || '');
    const [images, setImages] = useState([]);
    const [showDiscardModal, setShowDiscardModal] = useState(false);
    const navigate = useNavigate();
    const dropAreaRef = useRef(null);

    useEffect(() => {
        if (editingPostId) {
            setTitle(initialTitle);
            setDescription(initialDescription);
            setPrice(initialPrice);
        }
    }, [editingPostId, initialTitle, initialDescription, initialPrice]);

    const handleImageChange = (e) => {
        if (e.target.files) {
            setImages([...images, ...Array.from(e.target.files)]);
        }
    };

    const handleRemoveImage = (index) => {
        const newImages = images.filter((_, i) => i !== index);
        setImages(newImages);
    };

    const handleSharePost = () => {
        const formData = new FormData();
        images.forEach(image => {
            formData.append('images', image);
        });
        formData.append('title', title);
        formData.append('description', description);
        formData.append('price', price);
        formData.append('email', user.email);

        axios.post('http://localhost:8081/create-post', formData)
            .then(res => {
                if (res.data.message === "Success") {
                    navigate('/profile');
                } else {
                    console.log("Failed to create post");
                }
            })
            .catch(err => {
                console.log("Error occurred while creating post", err);
            });
    };

    const handleCancelPost = () => {
        setShowDiscardModal(true);
    };

    const handleDiscardPost = () => {
        setShowDiscardModal(false);
        navigate('/profile');
    };

    const handleDragOver = (e) => {
        e.preventDefault();
        dropAreaRef.current.classList.add('drag-over');
    };

    const handleDragLeave = () => {
        dropAreaRef.current.classList.remove('drag-over');
    };

    const handleDrop = (e) => {
        e.preventDefault();
        dropAreaRef.current.classList.remove('drag-over');
        const files = Array.from(e.dataTransfer.files);
        setImages([...images, ...files]);
    };

    return (
        <div className="create-post-container">
            <h2>{editingPostId ? 'Edit Post' : 'Publish a Post'}</h2>
            <input
                type="text"
                placeholder="Title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                className="form-control mt-2"
            />
            <textarea
                placeholder="Write a caption..."
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                className="form-control mt-2"
            ></textarea>
            <div className="price-input-container">
                <input
                    type="text"
                    placeholder="Price"
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                    className="form-control mt-2 price-input"
                />
                <span className="currency-symbol">â‚¬</span>
            </div>
            <div className="image-upload-section">
                <label htmlFor="file-input" className="image-upload-label">
                    Add or Drag & Drop up to {15 - images.length} images
                </label>
                <input
                    id="file-input"
                    type="file"
                    multiple
                    onChange={handleImageChange}
                    className="form-control mt-2"
                    style={{ display: 'none' }} // Hide the file input
                />
                <div
                    className="drag-drop-area"
                    ref={dropAreaRef}
                    onDragOver={handleDragOver}
                    onDragLeave={handleDragLeave}
                    onDrop={handleDrop}
                    onClick={() => document.getElementById('file-input').click()} // Open file dialog on click
                >
                    <div className="image-preview-container">
                        {images.map((image, index) => (
                            <div className="image-preview" key={index}>
                                <img
                                    src={URL.createObjectURL(image)}
                                    alt="Selected"
                                    className="selected-image"
                                />
                                <button
                                    className="remove-image-button"
                                    onClick={() => handleRemoveImage(index)}
                                >
                                    &times;
                                </button>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
            <button className="btn btn-primary mt-2" onClick={handleSharePost}>Share</button>
            <button className="btn btn-secondary mt-2" onClick={handleCancelPost}>Cancel</button>
            {showDiscardModal && (
                <div className="modal">
                    <div className="modal-content">
                        <p>Discard post?</p>
                        <p>If you leave, your edits won't be saved.</p>
                        <button className="btn btn-danger mt-2" onClick={handleDiscardPost}>Discard</button>
                        <button className="btn btn-secondary mt-2" onClick={() => setShowDiscardModal(false)}>Cancel</button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default CreatePost;
