import React, { useState } from 'react';
import axios from 'axios';
import './ChatModal.css';

const ChatModal = ({ post, user, onClose }) => {
    const [message, setMessage] = useState('');

    const handleSendMessage = async (e) => {
        e.preventDefault();

        if (!message.trim()) return;

        try {
            await axios.post('http://localhost:8081/messages', {
                chat_id: post.id,
                user_email: user.email,
                message: message
            });
            onClose();
        } catch (error) {
            console.error('Error sending message:', error);
        }
    };

    return (
        <div className="chat-modal">
            <div className="chat-modal-content">
                <span className="chat-modal-close" onClick={onClose}>&times;</span>
                <div className="chat-modal-header">
                    <img src={`http://localhost:8081/${post.picture}`} alt={post.title} className="chat-modal-pic" />
                    <h2>{post.title}</h2>
                </div>
                <form onSubmit={handleSendMessage}>
                    <textarea
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        placeholder="Type your message..."
                        className="chat-modal-textarea"
                    />
                    <button type="submit" className="chat-modal-send-btn">Send</button>
                </form>
            </div>
        </div>
    );
};

export default ChatModal;
