import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Inbox = ({ user }) => {
    const [chats, setChats] = useState([]);

    useEffect(() => {
        if (!user || !user.id) {
            console.error("User not found or user ID missing");
            return;
        }

        console.log(`Fetching inbox for user ID: ${user.id}`);
        
        axios.get(`http://localhost:8081/chats/${user.id}`)
            .then(res => {
                console.log('Fetched inbox data:', res.data);
                setChats(res.data);
            })
            .catch(err => {
                console.error('Error fetching inbox:', err);
            });
    }, [user]);

    return (
        <div className="inbox">
            <h1>Inbox</h1>
            {chats.map(chat => (
                <div key={chat.id}>
                    <p>{chat.seller_email}</p>
                </div>
            ))}
        </div>
    );
};

export default Inbox;
