import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Post.css';

function Post({ post }) {
    const navigate = useNavigate();
    const user = JSON.parse(localStorage.getItem('user')) || {};

    const handleClick = () => {
        if (post.user_email === user.email) {
            navigate(`/own-post/${post.id}`);
        } else {
            navigate(`/post/${post.id}`);
        }
    };

    return (
        <div className="post-card" onClick={handleClick}>
            <img src={`http://localhost:8081/${post.picture}`} alt={post.title} className="post-image" />
            <div className="post-content">
                <h2 className="post-title">{post.title}</h2>
                <p className="post-author">Author: {post.author}</p>
                <p className="post-user">Posted by: {post.user_name || 'Unknown'}</p>
                <p className="post-price">Price: â‚¬{parseFloat(post.price).toFixed(2)}</p>
                <p className="post-genre">Genre: {post.genre}</p>
            </div>
        </div>
    );
}

export default Post;
