import React from 'react';
import './Signup.css';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';

function Signup() {
    const validationSchema = Yup.object().shape({
        name: Yup.string().required('Name is required'),
        email: Yup.string().required('Email is required').email('Email is invalid'),
        password: Yup.string().required('Password is required'),
    });

    const { register, handleSubmit, formState: { errors } } = useForm({
        resolver: yupResolver(validationSchema)
    });

    function onSubmit(data) {
        console.log(data);
    }

    return (
        <div className="signup-wrapper">
            <div className="signup-container">
                <h1>Book-Swap</h1>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <div className="form-group">
                        <input name="name" type="text" {...register('name')} placeholder="Name" className={`form-control ${errors.name ? 'is-invalid' : ''}`} />
                        <div className="invalid-feedback">{errors.name?.message}</div>
                    </div>
                    <div className="form-group">
                        <input name="email" type="text" {...register('email')} placeholder="Email" className={`form-control ${errors.email ? 'is-invalid' : ''}`} />
                        <div className="invalid-feedback">{errors.email?.message}</div>
                    </div>
                    <div className="form-group">
                        <input name="password" type="password" {...register('password')} placeholder="Password" className={`form-control ${errors.password ? 'is-invalid' : ''}`} />
                        <div className="invalid-feedback">{errors.password?.message}</div>
                    </div>
                    <button type="submit" className="btn btn-primary">Sign up</button>
                </form>
            </div>
            <div className="bottom-box">
                <p>Have an account? <a href="/login">Log in</a></p>
            </div>
        </div>
    );
}

export default Signup;
