import React from 'react';
import './PostDetail.css';

const PostDetail = ({ post }) => {
    if (!post) {
        return <div>Loading...</div>;
    }

    return (
        <div className="post-detail-container">
            <div className="post-detail">
                <h1 className="post-title">{post.title}</h1>
                <div className="post-detail-content">
                    <img src={post.imageUrl} alt={post.title} className="post-image" />
                    <div className="post-info">
                        <div className="post-price">Price: ‚Ç¨{post.price.toFixed(2)}</div>
                        <div className="post-author">
                            <img src={post.authorAvatar} alt={post.author} className="author-avatar" />
                            <span>{post.author}</span>
                        </div>
                        <p className="post-description">{post.description}</p>
                        <div className="post-actions">
                            <button className="buy-now">Buy Now</button>
                            <button className="add-to-cart">Add to Cart</button>
                            <button className="add-to-wishlist">Add to Wishlist</button>
                            <button className="contact-seller">Contact Seller</button>
                        </div>
                        <div className="post-meta">
                            <span>Posted by: {post.postedBy}</span>
                            <div className="post-likes">
                                <span role="img" aria-label="thumbs up">üëç</span> {post.likes}
                                <span role="img" aria-label="thumbs down">üëé</span> {post.dislikes}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default PostDetail;
