import React from 'react';
import './Post.css';

function Post({ post }) {
    return (
        <div className="post">
            <img src={`http://localhost:8081/${post.picture}`} alt={post.title} className="post-image" />
            <div className="post-details">
                <h3>{post.title}</h3>
                <p>{post.description}</p>
                <p><strong>Posted by:</strong> {post.author}</p>
                <p><strong>Price:</strong> â‚¬{post.price}</p>
                <p><strong>Genre:</strong> {post.genre}</p>
                <p><strong>Date:</strong> {new Date(post.time).toLocaleDateString()}</p>
            </div>
        </div>
    );
}

export default Post;
