import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Inbox.css';

const Inbox = () => {
  const [conversations, setConversations] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  useEffect(() => {
    axios.get('http://localhost:8081/inbox')
      .then(res => setConversations(res.data))
      .catch(err => console.error(err));
  }, []);

  const selectChat = (chatId) => {
    setSelectedChat(chatId);
    axios.get(`http://localhost:8081/messages/${chatId}`)
      .then(res => setMessages(res.data))
      .catch(err => console.error(err));
  };

  const sendMessage = () => {
    if (newMessage.trim() === '') return;

    axios.post('http://localhost:8081/sendMessage', {
      chatId: selectedChat,
      message: newMessage
    })
      .then(res => {
        setMessages([...messages, res.data]);
        setNewMessage('');
      })
      .catch(err => console.error(err));
  };

  return (
    <div className="inbox-container">
      <div className="conversations-list">
        {conversations.map(conv => (
          <div key={conv.id} onClick={() => selectChat(conv.id)} className="conversation-item">
            <img src={conv.profilePic} alt="profile" className="profile-pic" />
            <span>{conv.name}</span>
          </div>
        ))}
      </div>
      <div className="chat-window">
        {selectedChat && (
          <>
            <div className="chat-header">
              <img src={conversations.find(conv => conv.id === selectedChat).profilePic} alt="profile" className="profile-pic" />
              <span className="chat-user-name">{conversations.find(conv => conv.id === selectedChat).name}</span>
            </div>
            <div className="messages-list">
              {messages.map((msg, index) => (
                <div key={index} className="message-item">
                  {msg.postImage && (
                    <div className="post-image-container">
                      <img src={`http://localhost:8081/${msg.postImage}`} alt="post" className="post-image" />
                      <span>{msg.postTitle}</span>
                    </div>
                  )}
                  <div className="message-content">{msg.message}</div>
                  <span className="message-timestamp">{new Date(msg.timestamp).toLocaleString()}</span>
                </div>
              ))}
            </div>
            <div className="chat-input-container">
              <input
                type="text"
                value={newMessage}
                onChange={e => setNewMessage(e.target.value)}
                className="chat-input"
                placeholder="Type your message..."
              />
              <button onClick={sendMessage} className="send-button">Send</button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Inbox;
