import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Inbox.css';

function Inbox({ user }) {
    const [chats, setChats] = useState([]);
    const [selectedChatId, setSelectedChatId] = useState(null);

    useEffect(() => {
        const fetchChats = async () => {
            try {
                const res = await axios.get(`http://localhost:8081/chats/${user.id}`);
                setChats(res.data);
            } catch (error) {
                console.error('Error fetching chats:', error);
            }
        };

        fetchChats();
    }, [user.id]);

    return (
        <div className="inbox-container">
            <div className="chat-list">
                {chats.map((chat) => (
                    <div
                        key={chat.id}
                        className={`chat-item ${selectedChatId === chat.id ? 'selected' : ''}`}
                        onClick={() => setSelectedChatId(chat.id)}
                    >
                        <img src={`http://localhost:8081/${chat.postImage}`} alt={chat.postTitle} className="profile-picture" />
                        <div className="chat-info">
                            <h3>{chat.postTitle}</h3>
                            <p>{chat.lastMessage}</p>
                        </div>
                    </div>
                ))}
            </div>
            <div className="chat-content">
                {selectedChatId && <Chat chatId={selectedChatId} user={user} />}
            </div>
        </div>
    );
}

export default Inbox;
