import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './Chat.css';

function Chat({ match }) {
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const [chatInfo, setChatInfo] = useState({});
    const messagesEndRef = useRef(null);

    const chatId = match.params.id;
    const user = JSON.parse(localStorage.getItem('user'));

    useEffect(() => {
        axios.get(`http://localhost:8081/chat/${chatId}`)
            .then(res => {
                setMessages(res.data.messages);
                setChatInfo(res.data.chatInfo);
            })
            .catch(err => {
                console.error("Error fetching chat:", err);
            });
    }, [chatId]);

    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);

    const handleSendMessage = () => {
        if (newMessage.trim() === '') return;
        axios.post(`http://localhost:8081/chat/${chatId}/message`, { message: newMessage, userId: user.id })
            .then(res => {
                setMessages(prevMessages => [...prevMessages, res.data]);
                setNewMessage('');
            })
            .catch(err => {
                console.error("Error sending message:", err);
            });
    };

    return (
        <div className="chat-container">
            <div className="chat-header">
                <h2>{chatInfo.name}</h2>
            </div>
            <div className="chat-messages">
                {messages.map((msg, index) => (
                    <div key={index} className={`chat-message ${msg.sender === user.email ? 'own-message' : ''}`}>
                        <span className="message-content">{msg.message}</span>
                    </div>
                ))}
                <div ref={messagesEndRef} />
            </div>
            <div className="chat-input">
                <input 
                    type="text" 
                    placeholder="Type your message..." 
                    value={newMessage} 
                    onChange={(e) => setNewMessage(e.target.value)}
                />
                <button onClick={handleSendMessage}>Send</button>
            </div>
        </div>
    );
}

export default Chat;
