import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './Chat.css';

function Chat() {
    const { chatId, userId } = useParams();
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');

    useEffect(() => {
        fetchMessages();
    }, []);

    const fetchMessages = async () => {
        try {
            const res = await axios.get(`http://localhost:8081/chat/${chatId}/${userId}`);
            setMessages(res.data);
        } catch (err) {
            console.log("Error fetching messages:", err);
        }
    };

    const handleSendMessage = async () => {
        if (newMessage.trim() === '') return;
        const messageData = {
            chat_id: chatId,
            user_email: localStorage.getItem('user_email'),
            message: newMessage
        };

        try {
            await axios.post('http://localhost:8081/send-message', messageData);
            setNewMessage('');
            fetchMessages();
        } catch (err) {
            console.log("Error sending message:", err);
        }
    };

    return (
        <div className="chat-container">
            <h2>Chat</h2>
            <ul className="message-list">
                {messages.map((msg) => (
                    <li
                        key={msg.id}
                        className={`message-item ${msg.user_email === localStorage.getItem('user_email') ? 'sent' : 'received'}`}
                    >
                        <span>{msg.message}</span>
                    </li>
                ))}
            </ul>
            <div className="message-form">
                <input
                    type="text"
                    placeholder="Type your message..."
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                />
                <button onClick={handleSendMessage}>Send</button>
            </div>
        </div>
    );
}

export default Chat;
