import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Home.css';
import Layout from './Layout';

function Home() {
  const [posts, setPosts] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [filter, setFilter] = useState({ sortBy: '', genre: '' });

  useEffect(() => {
    fetchPosts();
  }, [filter]);

  const fetchPosts = async () => {
    try {
      const res = await axios.get('http://localhost:8081/posts', { params: { ...filter, searchTerm: searchQuery } });
      const postsWithUsernames = await Promise.all(res.data.map(async (post) => {
        try {
          const userRes = await axios.get(`http://localhost:8081/user/${post.user_email}`);
          return { ...post, user_name: userRes.data.name };
        } catch (err) {
          console.error(`Error fetching user data for ${post.user_email}:`, err);
          return { ...post, user_name: 'Unknown' };
        }
      }));
      setPosts(postsWithUsernames);
    } catch (err) {
      console.log("Error fetching posts:", err);
    }
  };

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  const handleFilterChange = (newFilter) => {
    setFilter(newFilter);
  };

  return (
    <Layout onSearch={handleSearch} onFilterChange={handleFilterChange}>
      <div className="home-container">
        <div className="posts-list">
          {posts.length ? posts.map(post => (
            <div key={post.id} className="post-card">
              <Link to={`/post/${post.id}`}>
                <img src={`http://localhost:8081/${post.picture}`} alt={post.title} className="post-image" />
              </Link>
              <div className="post-details">
                <Link to={`/post/${post.id}`} className="post-title">{post.title}</Link>
                <p className="post-author">Author: {post.author}</p>
                <p className="post-user">Posted by: {post.user_name}</p>
                <p className="post-price">Price: â‚¬{parseFloat(post.price).toFixed(2)}</p>
                <p className="post-genre">Genre: {post.genre}</p>
              </div>
            </div>
          )) : <p>No posts found</p>}
        </div>
      </div>
    </Layout>
  );
}

export default Home;
