import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Inbox.css';
import { useNavigate } from 'react-router-dom';

function Inbox() {
    const [chats, setChats] = useState([]);
    const [selectedChat, setSelectedChat] = useState(null);
    const navigate = useNavigate();
    const user = JSON.parse(localStorage.getItem('user'));

    useEffect(() => {
        if (user && user.id) {
            axios.get(`http://localhost:8081/inbox/${user.id}`)
                .then(res => {
                    setChats(res.data);
                })
                .catch(err => {
                    console.error("Error fetching inbox:", err);
                });
        }
    }, [user]);

    const handleChatClick = (chatId) => {
        setSelectedChat(chatId);
    };

    return (
        <div className="inbox-container">
            <div className="inbox-list">
                <h1>Inbox</h1>
                {chats.map(chat => (
                    chat.name !== user.name && (
                        <div key={chat.id} onClick={() => handleChatClick(chat.id)} className="inbox-item">
                            <img src={chat.profile_pic} alt={chat.name} className="profile-pic" />
                            <div>
                                <strong>{chat.name}</strong>
                                <p>{chat.lastMessage}</p>
                            </div>
                        </div>
                    )
                ))}
            </div>
            <div className="chat-window">
                {selectedChat ? (
                    <Chat chatId={selectedChat} />
                ) : (
                    <div className="chat-placeholder">Your messages<br />Send a message to start a chat.</div>
                )}
            </div>
        </div>
    );
}

export default Inbox;
