import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Inbox.css';

function Inbox() {
    const [chats, setChats] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchChats = async () => {
            const user = JSON.parse(localStorage.getItem('user'));
            const response = await axios.get(`http://localhost:8081/chats?user_id=${user.id}`);
            const chatData = await Promise.all(response.data.map(async chat => {
                const otherUserId = chat.buyer_id === user.id ? chat.seller_id : chat.buyer_id;
                const otherUserResponse = await axios.get(`http://localhost:8081/user/${otherUserId}`);
                return {
                    ...chat,
                    otherUser: otherUserResponse.data
                };
            }));
            setChats(chatData);
        };

        fetchChats();
    }, []);

    const handleChatClick = (chatId) => {
        navigate(`/chat/${chatId}`);
    };

    return (
        <div className="inbox-container">
            <div className="inbox-header">Inbox</div>
            <ul className="chat-list">
                {chats.map(chat => (
                    <li
                        key={chat.id}
                        className="chat-list-item"
                        onClick={() => handleChatClick(chat.id)}
                    >
                        <img
                            src={`http://localhost:8081/${chat.otherUser.profile_pic}`}
                            alt={chat.otherUser.name}
                            className="chat-profile-pic"
                        />
                        <div className="chat-user-info">
                            <span className="chat-user-name">{chat.otherUser.name}</span>
                            <span className="chat-last-message">Last message...</span>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Inbox;
