import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import './PostDetail.css';

const PostDetail = ({ user }) => {
    const [post, setPost] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [message, setMessage] = useState('');
    const [chatId, setChatId] = useState(null);
    const navigate = useNavigate();
    const { id } = useParams();

    useEffect(() => {
        const fetchPost = async () => {
            try {
                const res = await axios.get(`http://localhost:8081/post/${id}`);
                setPost(res.data);
            } catch (error) {
                console.error('Error fetching post:', error);
            }
        };
        fetchPost();
    }, [id]);

    const handleContactSeller = async () => {
        if (!user) {
            console.error('User not logged in');
            return;
        }

        if (!post || !post.user_id) {
            console.error('Post or seller_id is not defined');
            return;
        }

        try {
            // Ensure the chat exists or create a new one
            const res = await axios.post('http://localhost:8081/createChat', {
                buyer_id: user.id,
                seller_id: post.user_id
            });

            setChatId(res.data.chat_id);
            setShowModal(true);
        } catch (error) {
            console.error('Error creating or fetching chat:', error);
        }
    };

    const handleSendMessage = async () => {
        if (!message.trim()) return;

        try {
            await axios.post('http://localhost:8081/messages', {
                chat_id: chatId,
                user_email: user.email,
                message,
                post_id: post.id // Include post ID
            });
            setShowModal(false);
            setMessage('');
            navigate(`/inbox/${chatId}`);
        } catch (error) {
            console.error('Error sending message:', error);
        }
    };

    if (!post) {
        return <div>Loading...</div>;
    }

    return (
        <div className="post-detail">
            <h2>{post.title}</h2>
            <p>{post.description}</p>
            <button className="contact-seller-button" onClick={handleContactSeller}>Contact Seller</button>

            {showModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h3>Contact Seller</h3>
                        <textarea 
                            value={message}
                            onChange={(e) => setMessage(e.target.value)}
                            placeholder="Type your message..."
                        />
                        <button onClick={handleSendMessage}>Send</button>
                        <button onClick={() => setShowModal(false)}>Cancel</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default PostDetail;
