import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';

function Home() {
    const [user, setUser] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const userData = localStorage.getItem('user');
        if (userData) {
            setUser(JSON.parse(userData));
        } else {
            navigate('/'); // Redirect to login if user data is not found
        }
    }, [navigate]);

    if (!user) {
        return <div>Loading...</div>;
    }

    return (
        <div className="home-container">
            <header className="home-header">
                <div className="greeting">
                    <h1>Welcome, {user.name}!</h1>
                    <p>We're glad to see you again.</p>
                </div>
                <div className="profile-button-container">
                    <img src={`http://localhost:8081/${user.profile_pic}`} alt="Profile" className="profile-pic-small" />
                    <button className="btn btn-primary profile-button" onClick={() => navigate('/profile')}>
                        Profile
                    </button>
                </div>
            </header>
            <main className="home-main">
                <section className="quick-actions">
                    <h2>Quick Actions</h2>
                    <div className="actions">
                        <button className="btn btn-secondary" onClick={() => navigate('/dashboard')}>
                            View Dashboard
                        </button>
                        <button className="btn btn-secondary" onClick={() => navigate('/settings')}>
                            Settings
                        </button>
                    </div>
                </section>
                <section className="recent-activity">
                    <h2>Recent Activity</h2>
                    <ul>
                        <li>Logged in from a new device</li>
                        <li>Changed password</li>
                        <li>Updated profile picture</li>
                    </ul>
                </section>
            </main>
            <footer className="home-footer">
                <p>&copy; 2024 Your Company. All rights reserved.</p>
                <nav>
                    <a href="/terms">Terms of Service</a>
                    <a href="/privacy">Privacy Policy</a>
                    <a href="/contact">Contact Us</a>
                </nav>
            </footer>
        </div>
    );
}

export default Home;
