import React from 'react';
import './Post.css';

const Post = ({ post }) => {
    return (
        <div className="post-card">
            <img src={`http://localhost:8081/${post.picture}`} alt={post.title} />
            <h3>{post.title}</h3>
            <p>{post.description}</p>
            <div className="post-meta">
                <span><strong>Author:</strong> {post.author}</span>
                <span><strong>Posted by:</strong> {post.user}</span>
                <span><strong>Price:</strong> â‚¬{post.price}</span>
                <span><strong>Genre:</strong> {post.genre}</span>
                <span><strong>Date:</strong> {new Date(post.time).toLocaleDateString()}</span>
            </div>
        </div>
    );
};

export default Post;
