import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Post from './Post';
import FilterModal from './FilterModal';
import './Home.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faFilter } from '@fortawesome/free-solid-svg-icons';

function Home() {
    const [posts, setPosts] = useState([]);
    const [filteredPosts, setFilteredPosts] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [filterTerm, setFilterTerm] = useState('');
    const [sortOption, setSortOption] = useState('');
    const [showFilterModal, setShowFilterModal] = useState(false);

    useEffect(() => {
        axios.get('http://localhost:8081/posts')
            .then(response => {
                setPosts(response.data);
                setFilteredPosts(response.data);
            })
            .catch(error => {
                console.log('Error fetching posts:', error);
            });
    }, []);

    useEffect(() => {
        applyFilters();
    }, [searchTerm, filterTerm, sortOption]);

    const applyFilters = () => {
        let tempPosts = posts;

        if (searchTerm) {
            tempPosts = tempPosts.filter(post =>
                post.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                post.author.toLowerCase().includes(searchTerm.toLowerCase()) ||
                post.description.toLowerCase().includes(searchTerm.toLowerCase())
            );
        }

        if (filterTerm) {
            tempPosts = tempPosts.filter(post => post.genre === filterTerm);
        }

        if (sortOption) {
            if (sortOption === 'Newest First') {
                tempPosts = tempPosts.sort((a, b) => new Date(b.time) - new Date(a.time));
            } else if (sortOption === 'Oldest First') {
                tempPosts = tempPosts.sort((a, b) => new Date(a.time) - new Date(b.time));
            } else if (sortOption === 'Price Ascending') {
                tempPosts = tempPosts.sort((a, b) => a.price - b.price);
            } else if (sortOption === 'Price Descending') {
                tempPosts = tempPosts.sort((a, b) => b.price - a.price);
            }
        }

        setFilteredPosts(tempPosts);
    };

    const handleSearchChange = (event) => {
        setSearchTerm(event.target.value);
    };

    const handleFilterChange = (event) => {
        setFilterTerm(event.target.value);
    };

    const handleSortChange = (event) => {
        setSortOption(event.target.value);
    };

    const toggleFilterModal = () => {
        setShowFilterModal(!showFilterModal);
    };

    return (
        <div className="home">
            <div className="search-bar">
                <input
                    type="text"
                    placeholder="Search..."
                    value={searchTerm}
                    onChange={handleSearchChange}
                />
                <button className="search-button">
                    <FontAwesomeIcon icon={faSearch} />
                </button>
                <button className="filter-button" onClick={toggleFilterModal}>
                    <FontAwesomeIcon icon={faFilter} />
                </button>
            </div>
            {showFilterModal && (
                <FilterModal
                    sortOption={sortOption}
                    filterTerm={filterTerm}
                    onSortChange={handleSortChange}
                    onFilterChange={handleFilterChange}
                    onClose={toggleFilterModal}
                />
            )}
            {filteredPosts.length > 0 ? (
                filteredPosts.map(post => (
                    <Post key={post.id} post={post} />
                ))
            ) : (
                <div className="no-posts">No posts available</div>
            )}
        </div>
    );
}

export default Home;
