import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Inbox() {
    const [chats, setChats] = useState([]);
    const navigate = useNavigate();
    const user = JSON.parse(localStorage.getItem('user')); // Get logged-in user info

    useEffect(() => {
        fetchChats();
    }, []);

    const fetchChats = async () => {
        try {
            const res = await axios.get(`http://localhost:8081/inbox/${user.id}`);
            setChats(res.data);
        } catch (err) {
            console.error("Error fetching chats:", err);
        }
    };

    const handleChatClick = (chatId) => {
        navigate(`/chat/${chatId}`);
    };

    return (
        <div className="inbox-container">
            <h2>Inbox</h2>
            {chats.length > 0 ? (
                <ul className="chat-list">
                    {chats.map(chat => (
                        <li key={chat.id} className="chat-item" onClick={() => handleChatClick(chat.id)}>
                            <p>{chat.buyer_id === user.id ? chat.seller_name : chat.buyer_name}</p>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No chats available.</p>
            )}
        </div>
    );
}

export default Inbox;
