import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Home.css';
import Layout from './Layout';

function Home() {
  const [posts, setPosts] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [filter, setFilter] = useState({ sortBy: '', genre: '' });

  useEffect(() => {
    fetchPosts();
  }, [filter, searchQuery]);

  const fetchPosts = () => {
    axios.get('http://localhost:8081/posts', { params: { ...filter, searchTerm: searchQuery } })
      .then(res => {
        const postsWithUsernames = res.data.map(post => ({
          ...post,
          user_name: 'Unknown', // Default value
        }));

        // Fetch user names for each post
        const userFetches = postsWithUsernames.map(post =>
          axios.get(`http://localhost:8081/user/${post.user_email}`)
            .then(userRes => {
              return { ...post, user_name: userRes.data.name }; // Correctly map the user's name
            })
            .catch(err => {
              console.error(`Error fetching user data for ${post.user_email}:`, err);
              return post; // Return the post with default user name in case of an error
            })
        );

        // Wait for all user data fetches to complete
        Promise.all(userFetches).then(updatedPosts => {
          setPosts(updatedPosts);
        });
      })
      .catch(err => {
        console.log("Error fetching posts:", err);
      });
  };

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  const handleFilterChange = (newFilter) => {
    setFilter(newFilter);
  };

  return (
    <Layout onSearch={handleSearch} onFilterChange={handleFilterChange}>
      <div className="home-container">
        <div className="posts-list">
          {posts.length ? posts.map(post => (
            <div key={post.id} className="post-card">
              <Link to={`/post/${post.id}`}>
                <img src={`http://localhost:8081/${post.picture}`} alt={post.title} className="post-image" />
              </Link>
              <div className="post-details">
                <Link to={`/post/${post.id}`} className="post-title">{post.title}</Link>
                <p className="post-author">Author: {post.author}</p>
                <p className="post-user">Posted by: {post.user_name}</p>
                <p className="post-price">Price: â‚¬{parseFloat(post.price).toFixed(2)}</p>
                <p className="post-genre">Genre: {post.genre}</p>
              </div>
            </div>
          )) : <p>No posts found</p>}
        </div>
      </div>
    </Layout>
  );
}

export default Home;
